tables.sqls:
CREATE TABLE IF NOT EXISTS terminal ( id INTEGER PRIMARY KEY AUTOINCREMENT, addr VARCHAR(40) NOT NULL UNIQUE, status INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS terminalInfo ( id INTEGER PRIMARY KEY AUTOINCREMENT, terminal_id INTEGER, current INTEGER, longitude REAL, latitude REAL, height INTEGER, date_time DATETIME, FOREIGN KEY (terminal_id) REFERENCES terminal(id));
CREATE VIEW IF NOT EXISTS v_terminal as select terminalInfo.id,terminal.addr,terminal.status,terminalInfo.current,terminalInfo.longitude,terminalInfo.latitude,terminalInfo.height,terminalInfo.date_time from terminal,terminalInfo where terminal.id=terminalInfo.terminal_id
